import {
  EyeFilled,
  LikeFilled,
  MessageFilled
} from "@ant-design/icons";
import styled from "@emotion/styled";
import { Avatar, Flex, Image, Space, theme, Typography } from "antd";
import { useNavigate } from "react-router-dom";
import BookmarkToggle from "./BookmarkToggle";
const { Text, Title } = Typography;

const Card = styled.div`
  height: 100%;
  min-height: 150px;
  padding: 16px;
  cursor: pointer;
  border-bottom: 1px solid #f0f0f0;
`;

const CardContent = styled.div`
  display: flex;
  gap: 16px;
`;

function PostCard({ post = {}, style}) {
  const navigate = useNavigate();
  const { token } = theme.useToken();

  return (
    <Card>
      <Flex justify="space-between">
        <Space style={{ marginBottom: 10 }}>
          <Avatar
            size={"small"}
            style={{ backgroundColor: token.colorPrimary }}
            src="https://shanibacreative.com/wp-content/uploads/2020/06/membuat-foto-profil-yang-bagus.jpg"
          />
          <Text>{post?.author?.name}</Text>|
          <Text
            className="cs-ellipsis-1"
            style={{ marginRight: 8, fontWeight: "600", color: token.colorPrimary }}
          >
            {post.category.name}
          </Text>
        </Space>
      </Flex>
      <CardContent style={style}>
        <Image
          width={150}
          height={150}
          style={{
            objectFit: "cover",
            objectPosition: "top",
          }}
          src={post.thumbnail}
          fallback="data:image/png;base64,"
        />
        <Flex
          vertical
          flex={1}
          onClick={() => navigate(`/posts/${post.slug}`, { state: { post } })}
        >
          <Flex justify="space-between">
            <Title level={3} style={{ marginBottom: 8, fontWeight: "700" }} ellipsis={{ rows: 2 }}>
              {post.title}
            </Title>
            <BookmarkToggle isMark={post?.marked} postId={post?.id} />
          </Flex>
          <Title
            level={5}
            style={{ marginBottom: 16, fontWeight: "normal", color: "rgb(107, 107, 107)" }}
            ellipsis={{ rows: 2 }}
          >
            Lorem ipsum dolor sit amet consectetur adipisicing elit. Saepe esse laudantium, possimus
            vero delectus voluptatum illo et. Tempora pariatur totam labore, qui magni dignissimos
            id assumenda at deserunt alias cupiditate.
          </Title>
          <Flex justify="space-between" style={{ marginTop: "auto" }}>
            <Text style={{ textWrap: "nowrap" }}>{post.published_at}</Text>

            <Flex gap={16} justify="end" style={{ marginTop: "6px", marginRight: "8px" }}>
              <IconText icon={EyeFilled} text={100} />
              <IconText icon={MessageFilled} text={post?.comments} />
              <IconText icon={LikeFilled} text={post?.likes} />
            </Flex>
          </Flex>
        </Flex>
      </CardContent>
    </Card>
  );
}

function IconText({ icon: Icon, text }) {
  const { token } = theme.useToken();
  return (
    <Flex align="center" gap={4}>
      <Icon style={{ color: token.colorTextSecondary }} />{" "}
      <Text style={{ color: token.colorTextSecondary }}>{text}</Text>
    </Flex>
  );
}

export default PostCard;
